import "./index.css";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { FetchMealById } from "../../services/mealService";

import SearchBox from "../../components/BarraBusqueda";
import BotonHome from "../../components/BotonHome";

const DetallePlato = () => {
  const { idPlato } = useParams();
  const [plato, setPlato] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const getPlato = async () => {
      try {
        const response = await FetchMealById();
        setPlato(response);
      } catch (error) {
        console.error(error);
      } finally {
        setLoading(false);
      }
    };
    getPlato();
  }, [idPlato]);

  return (
    <>
      {!loading && plato && <div class="Detalle">
		
		<div className="Header">
        <BotonHome /> <SearchBox />
      </div>
	  </div>}
      {loading && <p>Cargando...</p>}
      {!loading && !plato && <p>404 - No se ha encontrado el plato.</p>}
    </>
  );
};

export default DetallePlato;
